import React  from "react";

type ListPropsType= {
    title: string
    tasks: Task[] // Array<Task>

}

export type Task = {  // экспорт для проверки типизации
    id: number, 
    title: string, 
    isDone:  boolean
}

export const Todolist = ({title, tasks}:ListPropsType) => {
    const tittle = {title}.title // вместо {props.title}
    return (
        <div>
            <h3>{tittle}</h3>
            <div>
                <input />
                <button>+</button>
            </div>
            <ul>
                {tasks.map((task) =>{
                    debugger
                return (
                <li key={task.id}><input type="checkbox" checked={task.isDone} /> <span>{task.title}</span></li>  )
                })}
                
            

            </ul>
            <div>
                <button>All</button>
                <button>Active</button>
                <button>Completed</button>
            </div>
        </div>
    )
}
